class Date {    private final int day;    private final String month;    private final int year;    public Date(int day, String month, int year) {        this.day = day;        this.month = month;        this.year = year;    }    public int getDay() {        return day;    }    public String getMonth() {        return month;    }    public int getYear() {        return year;    }    public boolean isBefore(Date other) {        return this.year < other.year ||                (this.year == other.year && (monthToNum(this.month)                        < monthToNum(other.month) ||                        (monthToNum(this.month) == monthToNum(other.month)                                && this.day < other.day)));    }    private int monthToNum(String month) {        return switch (month.toLowerCase()) {            case "jan" -> 1;            case "feb" -> 2;            case "mar" -> 3;            case "apr" -> 4;            case "may" -> 5;            case "jun" -> 6;            case "jul" -> 7;            case "aug" -> 8;            case "sep" -> 9;            case "oct" -> 10;            case "nov" -> 11;            case "dec" -> 12;            default -> 0;        };    }}